interface propsCard {
  children: React.ReactElement[]
  src: string
  alt: string
  titleProject: string
  descriptionProject: string
  repositoryhref?: string
  projecthref: string
  isRepository?: boolean
  posdata?: string
}

function Card({
  src,
  alt,
  titleProject,
  descriptionProject,
  children,
  isRepository = false,
  projecthref,
  repositoryhref = '',
  posdata = '',
}: propsCard) {
  return (
    // <Link href={href} target="_blank" className={style.cardLink}>
    <div className={style.card}>
      <div className={style.cardContainerIcons}>{children}</div>
      <Image
        className={style.cardImage}
        width={1000}
        height={1000}
        src={src}
        alt={alt}
      ></Image>
      <div className={style.cardContent}>
        {/* <div> */}
        <div className={style.cardContainerIcons}>{children}</div>
        <h3 className={style.cardH3}>
          <FormattedMessage id={titleProject} />
        </h3>

        <p className={style.cardP}>
          <FormattedMessage id={descriptionProject} />
        </p>
        <div className={style.cardContainerButton}>
          <Link
            target={'_blank'}
            className={style.cardButton}
            href={projecthref}
          >
            ir al proyecto
          </Link>
          {isRepository && (
            <Link
              target={'_blank'}
              className={style.cardButton}
              href={repositoryhref}
            >
              repositorio
            </Link>
          )}
        </div>
        <p className={style.cardPostdata}>{posdata}</p>
        {/* </div> */}
      </div>
    </div>
    // </Link>
  )
}
let CLICK = false

let active = false

export default function Carrusel({ children }: { children: React.ReactNode }) {
  const [numberCard, setNumberCard] = useState(1)
  const refSlider = useRef<HTMLDivElement | null>(null)
  const [autoPlay, setAutoPlay] = useState<NodeJS.Timer>()

  let childrenElement = 0
  useEffect(() => {
    try {
      if (!refSlider.current) throw new Error('ocurred on error')

      if (refSlider.current.childElementCount < 3) {
        // eslint-disable-next-line react-hooks/exhaustive-deps
        childrenElement = refSlider.current.childElementCount
        for (let index = 0; index < childrenElement; index++) {
          const children = refSlider.current.children
          const clone = children?.item(index)?.cloneNode(true)
          if (clone === undefined) return
          refSlider.current.appendChild(clone)
        }
        // NI NECESITO ESTE CONDICIONAL
        // if (refSlider.current.childElementCount > 2) {

        document.documentElement.style.setProperty(
          '--translate-card',
          'translateX(calc(-100% - var(--gap)))'
        )

        const cards = refSlider.current.children

        const translate = Number(
          getComputedStyle(document.documentElement)
            .getPropertyValue('--translate')
            .replace('px', '')
        )

        const gap = Number(
          getComputedStyle(document.documentElement)
            .getPropertyValue('--gap')
            .replace('px', '')
        )

        document.documentElement.style.setProperty(
          '--translate',
          `${translate - cards[numberCard].scrollWidth - gap}px`
        )

        const prevChild = refSlider.current.lastChild
        refSlider.current.prepend(prevChild!)

        const lastChild = refSlider.current.lastChild
        refSlider.current.prepend(lastChild!)
        // }

        CLICK = true
      }
    } catch (error) {
      console.log(error)
    }
  }, [])

  function play() {
    setAutoPlay(() =>
      setInterval(() => {
        next()
      }, 2000)
    )
  }
  useEffect(() => {
    play()
    return () => clearInterval(autoPlay)
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [])

  function reorderSlider() {
    if (refSlider.current === null) return

    const numberChildSlider = Number(refSlider.current.childElementCount)
    const cards = refSlider.current.children

    // WHEN TWO CARDS
    // numberCard === numberChildSlider  <----------

    // WHEN TRHEE CARDS
    // numberCard === numberChildSlider - 1  <---------

    // WHEN FOUR CARDS
    // numberCard === numberChildSlider - 2  <----------

    if (numberCard === numberChildSlider - 2) {
      const firstChild: Node | null = refSlider.current.firstChild

      refSlider.current.appendChild(firstChild!)
      const translate = Number(
        getComputedStyle(document.documentElement)
          .getPropertyValue('--translate')
          .replace('px', '')
      )

      const gap = Number(
        getComputedStyle(document.documentElement)
          .getPropertyValue('--gap')
          .replace('px', '')
      )

      document.documentElement.style.setProperty('--animation', 'none')

      document.documentElement.style.setProperty(
        '--translate',
        `${translate + cards[0].scrollWidth + gap}px`
      )
      setNumberCard((prev) => prev - 1)
    }

    if (numberCard === 0) {
      const lastChild = refSlider.current.lastChild
      refSlider.current.prepend(lastChild!)

      const translate = Number(
        getComputedStyle(document.documentElement)
          .getPropertyValue('--translate')
          .replace('px', '')
      )

      const gap = Number(
        getComputedStyle(document.documentElement)
          .getPropertyValue('--gap')
          .replace('px', '')
      )

      document.documentElement.style.setProperty('--animation', 'none')

      document.documentElement.style.setProperty(
        '--translate',
        `${translate - cards[numberCard].scrollWidth - gap}px`
      )
      setNumberCard((prev) => prev + 1)
    }
    CLICK = true
  }

  const next = () => {
    // FOR FOUR CARDS
    // if (refSlider.current.childElementCount < 6) {
    if (refSlider.current === null) return
    if (!active) {
      const prevChild = refSlider.current.firstChild
      refSlider.current.appendChild(prevChild!)
      active = true
      document.documentElement.style.setProperty('--translate-card', 'none')
    }
    // }

    if (!CLICK) return

    CLICK = false

    document.documentElement.style.setProperty(
      '--animation',
      'transform 0.5s ease-in-out'
    )

    const cards = refSlider.current.children

    const translate = Number(
      getComputedStyle(document.documentElement)
        .getPropertyValue('--translate')
        .replace('px', '')
    )

    const gap = Number(
      getComputedStyle(document.documentElement)
        .getPropertyValue('--gap')
        .replace('px', '')
    )

    document.documentElement.style.setProperty(
      '--translate',
      `${translate - cards[numberCard].scrollWidth - gap}px`
    )
    setNumberCard((prev) => prev + 1)
  }

  const prev = () => {
    if (!CLICK) return

    CLICK = false
    document.documentElement.style.setProperty(
      '--animation',
      'transform 0.5s ease-in-out'
    )

    const cards = refSlider?.current?.children

    if (cards === undefined) return
    const translate = Number(
      getComputedStyle(document.documentElement)
        .getPropertyValue('--translate')
        .replace('px', '')
    )

    const gap = Number(
      getComputedStyle(document.documentElement)
        .getPropertyValue('--gap')
        .replace('px', '')
    )

    document.documentElement.style.setProperty(
      '--translate',
      `${translate + cards[numberCard].scrollWidth + gap}px`
    )

    setNumberCard((prev) => prev - 1)
  }

  const mauseEnter = () => {
    clearInterval(autoPlay)
  }

  const mauseLeave = () => {
    play()
  }

  const prevDefault = () => {
    // if (!CLICK) return

    if (active) {
      document.documentElement.style.setProperty(
        '--translate-card',
        'translateX(calc(-100% - var(--gap)))'
      )

      const prevChild = refSlider?.current?.firstChild
      refSlider.current?.prepend(prevChild!)

      const lastChild = refSlider?.current?.lastChild
      refSlider.current?.prepend(lastChild!)
    }
    active = false
  }

  return (
    <div
      className={style.container}
      onMouseEnter={mauseEnter}
      onMouseLeave={mauseLeave}
    >
      <button
        className={`${style.button} ${style.buttonLeft}`}
        onClick={prev}
        onMouseEnter={prevDefault}
      >
        &#10096;
      </button>
      <div className={style.center}>
        <div
          className={style.slider}
          ref={refSlider}
          onTransitionEnd={reorderSlider}
        >
          {children}
        </div>
      </div>
      <button className={`${style.button} ${style.buttonRight}`} onClick={next}>
        &#10097;
      </button>
    </div>
  )
}

